import { db } from '@/services/firebase';
import { collection, getDocs, doc, getDoc, setDoc, deleteDoc } from 'firebase/firestore';
import { getAuth } from 'firebase/auth';
export default {
  name: 'CommunityPage',
  data() {
    return {
      users: [],
      currentUser: {},
      following: [],
      followers: [],
      activeTab: 'all',
      showUnfollowModal: false,
      unfollowingUser: null,
      processingUser: null,
      toast: {
        show: false,
        message: '',
        type: 'success'
      },
      darkMode: false
    };
  },
  computed: {
    filteredUsers() {
      if (this.activeTab === 'following') {
        return this.users.filter(user => this.following.includes(user.uid) && user.uid !== this.currentUser.uid);
      } else if (this.activeTab === 'followers') {
        return this.users.filter(user => this.followers.includes(user.uid) && user.uid !== this.currentUser.uid);
      }
      return this.users.filter(user => user.uid !== this.currentUser.uid);
    }
  },
  methods: {
    toggleDarkMode() {
      this.darkMode = !this.darkMode;
      if (this.darkMode) {
        document.documentElement.classList.add('dark-theme');
      } else {
        document.documentElement.classList.remove('dark-theme');
      }
      localStorage.setItem('darkMode', this.darkMode);
    },
    getUserInitials(name) {
      if (!name) return '?';
      const parts = name.split(' ');
      return parts.length > 1 ? `${parts[0][0]}${parts[parts.length - 1][0]}`.toUpperCase() : name.length > 1 ? `${name[0]}${name[1]}`.toUpperCase() : `${name[0]}`.toUpperCase();
    },
    isFollowing(userId) {
      return this.following.includes(userId);
    },
    handleUnfollowAction(user) {
      this.unfollowingUser = user;
      this.showUnfollowModal = true;
    },
    cancelUnfollow() {
      this.showUnfollowModal = false;
      this.unfollowingUser = null;
    },
    confirmUnfollow() {
      if (this.unfollowingUser) {
        this.unfollowUser(this.unfollowingUser.uid);
        this.showUnfollowModal = false;
        this.unfollowingUser = null;
      }
    },
    async followUser(userId) {
      this.processingUser = userId;
      const auth = getAuth();
      const currentUserId = auth.currentUser.uid;
      const followRef = doc(db, 'users', currentUserId, 'following', userId);
      const followerRef = doc(db, 'users', userId, 'followers', currentUserId);
      try {
        await setDoc(followRef, {
          timestamp: new Date()
        });
        await setDoc(followerRef, {
          timestamp: new Date()
        });
        if (!this.following.includes(userId)) {
          this.following.push(userId);
        }
        this.showToast('Successfully followed user', 'success');
      } catch (error) {
        console.error('Error following user:', error);
        this.showToast('Failed to follow user', 'error');
      } finally {
        this.processingUser = null;
      }
    },
    async unfollowUser(userId) {
      this.processingUser = userId;
      const auth = getAuth();
      const currentUserId = auth.currentUser.uid;
      const followRef = doc(db, 'users', currentUserId, 'following', userId);
      const followerRef = doc(db, 'users', userId, 'followers', currentUserId);
      try {
        await deleteDoc(followRef);
        await deleteDoc(followerRef);
        this.following = this.following.filter(id => id !== userId);
        if (this.activeTab === 'following') {
          this.$nextTick(() => {
            if (this.filteredUsers.length === 0 && this.following.length > 0) {
              this.activeTab = 'all';
            }
          });
        }
        this.showToast('Successfully unfollowed user', 'success');
      } catch (error) {
        console.error('Error unfollowing user:', error);
        this.showToast('Failed to unfollow user', 'error');
      } finally {
        this.processingUser = null;
      }
    },
    showToast(message, type = 'success') {
      this.toast = {
        show: true,
        message,
        type
      };
      setTimeout(() => {
        this.toast.show = false;
      }, 3000);
    },
    async fetchUsers() {
      try {
        const querySnapshot = await getDocs(collection(db, 'users'));
        this.users = querySnapshot.docs.map(doc => ({
          uid: doc.id,
          ...doc.data()
        }));
      } catch (error) {
        console.error('Error fetching users:', error);
        this.showToast('Error loading users', 'error');
      }
    },
    async fetchCurrentUser() {
      const auth = getAuth();
      if (auth.currentUser) {
        try {
          const userDoc = await getDoc(doc(db, 'users', auth.currentUser.uid));
          if (userDoc.exists()) {
            this.currentUser = {
              uid: userDoc.id,
              ...userDoc.data()
            };
          } else {
            this.showToast('Your user profile could not be found', 'error');
          }
        } catch (error) {
          console.error('Error fetching current user:', error);
          this.showToast('Error loading your profile', 'error');
        }
      } else {
        this.$router.push('/dash');
      }
    },
    async fetchFollowing() {
      const auth = getAuth();
      if (auth.currentUser) {
        try {
          const followingSnapshot = await getDocs(collection(db, 'users', auth.currentUser.uid, 'following'));
          this.following = followingSnapshot.docs.map(doc => doc.id);
        } catch (error) {
          console.error('Error fetching following:', error);
          this.showToast('Error loading your following list', 'error');
        }
      }
    },
    async fetchFollowers() {
      const auth = getAuth();
      if (auth.currentUser) {
        try {
          const followersSnapshot = await getDocs(collection(db, 'users', auth.currentUser.uid, 'followers'));
          this.followers = followersSnapshot.docs.map(doc => doc.id);
        } catch (error) {
          console.error('Error fetching followers:', error);
          this.showToast('Error loading your followers list', 'error');
        }
      }
    }
  },
  async created() {
    // Check for saved theme preference
    const savedTheme = localStorage.getItem('darkMode');
    if (savedTheme !== null) {
      this.darkMode = savedTheme === 'true';
      if (this.darkMode) {
        document.documentElement.classList.add('dark-theme');
      }
    } else if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
      this.darkMode = true;
      document.documentElement.classList.add('dark-theme');
    }
    try {
      await this.fetchCurrentUser();
      await Promise.all([this.fetchUsers(), this.fetchFollowing(), this.fetchFollowers()]);
    } catch (error) {
      console.error('Error initializing community page:', error);
      this.showToast('Error loading community data', 'error');
    }
  }
};
